import Terminal, { ColorMode, LineType } from './components/';
import { useState } from 'react';
import './App.css'


function App() {
    const [terminalLineData, setTerminalLineData] = useState([
        { type: LineType.Output, value: 'Welcome to the React Terminal UI Demo!' },
        { type: LineType.Input, value: 'Some previous input received' },
    ]);

    return (
        <div className="App">
            <h1 className="title">FreezingKas</h1>
            <nav></nav>
            <div className='terminal'>
                <Terminal name='max@freezingkas: ~'
                    colorMode={ColorMode.Dark}
                    lineData={terminalLineData}
                    onInput={terminalInput => console.log(`New terminal input received: '${terminalInput}'`)}
                    prompt='max@freezingkas ~ ' />

            </div>

        </div>


    );

}

export default App;
