import Terminal, { ColorMode, LineType } from '../src/components/terminal/index.tsx';
import { useState } from 'react';
import './App.css'
import particlesConfig from "./components/assets/particles.json";
import Particle from "react-particles-js";


function App() {
    const [terminalLineData, setTerminalLineData] = useState([
        { type: LineType.Output, value: 'Welcome to the React Terminal UI Demo!' },
        { type: LineType.Input, value: 'Some previous input received' },
    ]);

    return (
        <>
            <Particle params={particlesConfig} className="App-particles__container" />
            <div className="App">
                <h1 className="title">FreezingKas</h1>

                <nav className='navigation'>
                    <a className='link' href="">Write-ups</a>
                    <p className='pipe'>|</p>
                    <a className='link' href="">Autre chose</a>
                </nav>

                <div className='terminal'>
                    <Terminal name='max@freezingkas: ~'
                        colorMode={ColorMode.Dark}
                        lineData={terminalLineData}
                        onInput={terminalInput => console.log(`New terminal input received: '${terminalInput}'`)}
                        prompt='max@freezingkas ~ ' />

                </div>

            </div>
        </>

    );

}

export default App;
